Example code:

#include <stdio.h>
#include <stdlib.h>

int main(int argc, char **argv){
	int **map;

	(void)argc;
	(void)argv;
	map = (int **)malloc(sizeof(int *) * 4);
	if (map == NULL){
		return 1;
	}
	for (int i = 0; i < 4; i++){
		map[i] = (int *)malloc(sizeof(int) * 4);
		if (map[i] == NULL){
			while (i > 0){
				free(map[i - 1]);
				i--;
			}
			free(map);
			return 1;
		}
	}
	for (int i = 0; i < 4; i++){
		for (int j = 0; j < 4; j++){
			map[i][j] = i * 4 + j;
			printf("%2d ", map[i][j]);
		}
		printf("\n");
	}
	for (int i = 0; i < 4; i++){
		free(map[i]);
	}
	free(map);
}

